@import url("https://fonts.googleapis.com/css2?family=Montserrat:wght@500;900&family=Onest&display=swap");
@import url("./keyframes.css");

* {
    margin: 0;
    padding: 0;
    font-family: "Montserrat", sans-serif;
}
body {
    background-color: #2b87d1;
}

.before-game-start {
    height: 100vh;
    width: 100vw;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
}
.after-game-start {
    height: 100vh;
    width: 100vw;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
}

.level {
    font-size: xx-large;
    position: relative;
    bottom: 40px;
    color: rgb(189, 185, 185);
}
.current-level {
    color: white;
}
.boxes-container {
    height: 400px;
    width: 400px;
    display: flex;
    flex-wrap: wrap;
}
.boxes {
    height: 120px;
    width: 120px;
    background-color: #2472c0;
    margin: 5px;
    border-radius: 10px;
}

.glowing-logo {
    height: 115px;
    width: 110px;
    display: flex;
    flex-wrap: wrap;
    justify-content: space-between;
    /* animation: glowLogo 2s ease-out infinite; */
}

.square {
    height: 48px;
    width: 48px;
    border-radius: 10px;
    background-color: white;
    display: flex;
    align-items: center;
    justify-content: center;
}

.inside-square {
    height: 30px;
    width: 30px;
    background-color: #2b87d1;
}

.gameName {
    font-size: 80px;
    color: white;
}

.start-button {
    width: 110px;
    height: 50px;
    background-color: yellow;
    border: none;
    border-radius: 5px;
    font-size: large;
    font-weight: 500;
    margin-top: 20px;
    cursor: pointer;
}

.glow {
    opacity: 0;
    background-color: white;
    animation: glow 1s;
}

.wrong-answer-blink {
    opacity: 0;
    background-color: rgb(235, 85, 85);
    animation: wrongGlow 0.1s;
}

.after-game-ended{
    height: 100vh;
    width: 100vw;
    display: flex;
    justify-content: center;
    align-items: center;
    flex-direction: column;
}

.final-level{
    font-size: 90px;
    color: white;
}

.try-again{
    width: 110px;
    height: 50px;
    background-color: #a1c2db;
    color: black;
    border: none;
    border-radius: 5px;
    font-size: large;
    font-weight: 500;
    margin-top: 20px;
    cursor: pointer;
}
.hidden {
    display: none;
}
